class PriorityQueue:
    def __init__(self):
        self.elements = []
    
    def is_empty(self):
        return len(self.elements) == 0
    
    def insert(self, item, priority):
        heapq.heappush(self.elements, (priority, item))
    
    def remove(self):
        return heapq.heappop(self.elements)[1]

def heuristic(neighbor,h):
    heuristic_dict = destination[h]
    return heuristic_dict[neighbor]
    
def a_star(graph, start, dest):
    frontier = PriorityQueue()
    frontier.insert([(start, 0)], 0)
    explored = set()
    while not frontier.is_empty():
        # shortest available path
        path = frontier.remove()
        # frontier contains paths with final node unexplored
        node = path[-1][0]
        g_cost = path[-1][1]
        explored.add(node)
        # goal test:
        if node == dest:
            # return only path without cost
            return [x for x, y in path], g_cost
        for neighbor, time in graph[node]:
            cumulative_cost = g_cost + time
            f_cost = cumulative_cost + heuristic(neighbor, dest)
            new_path = path + [(neighbor, cumulative_cost)]
            frontier.insert(new_path, f_cost)
def simple_path(graph, start, end, path=[]):
    path = path + [start]
    if start == end:
        return path
    if not start in graph:
        return None
    for node in graph[start]:
        if node not in path:
            newpath = simple_path(graph, node, end, path)
            if newpath: return newpath
    return None
